## Setup MICE mids for various methods ##
#' Impute missing data and plot the results using the mice package. #' #' This is a wrapper around the functionality of the \href{https://amices.org/mice}{mice} and #' \href{https://amices.org/ggmice}{ggmice} packages for Multivariate Imputation by Chained Equations and visualisation #' of the resulting imputed datasets. Users should refer to the documentation for details of the options available. #' #' The wrapper uses \code{\link[mice]{futuremice}} to perform imputation in parallel using the same number of cores as #' the requested number of imputations. This speeds up the process but be wary if your computer has limited cores. #' #' @param df data.frame|tibble Data frame or tibble of original data. #' @param imputations int Number of imputations to perform. #' @param iterations int Number of iterations to perform for imputation. #' @param method str Method of imputation to use, see the Details section of \code{\link[mice]{mice}}. #' @param action str Action to take when running \code{\link[mice]{complete}}. #' @param include bool Logical of whether to include the original dataset in the output. #' @param seed int Seed for random number generation #' #' impute_data <- function(df = df_complete, outcome_var = "final_pathology", imputations = 4, iterations = 5, method = "pmm", action = "long", include = TRUE, continuous = c("albumin", "tsh_value", "lymphocytes", "monocyte", "size_nodule_mm"), categorical = c("ethnicity", "incidental_nodule", "palpable_nodule", "rapid_enlargement", "compressive_symptoms", "hypertension", "vocal_cord_paresis", "graves_disease", "hashimotos_thyroiditis", "family_history_thyroid_cancer", "exposure_radiation", "bta_u_classification", "solitary_nodule", "cervical_lymphadenopathy", "thy_classification"), seed = 123) { results <- list() ## Setup imputation results$mids <- df |> dplyr::select(-{{ outcome_var }}) |> mice::futuremice(m = imputations, method = method, n.core = imputations, iterations = iterations, parallelseed = seed) ## Generate output dataset, results$imputed <- results$mids |> mice::complete(action = "long", include = include) ## Convert the .imp variable which indicates the imputation set to factor with original dataset labelled as such results$imputed <- results$imputed |> dplyr::mutate(.imp = factor(.imp, levels = seq(0, imputations, 1), labels = c("Original", as.character(seq(1, imputations, 1))))) ## We need to bind the outcome variable to each imputed dataset so they can be used in analyses outcome = df[[outcome_var]] n = imputations while(n > 0) { outcome = append(outcome, df[[outcome_var]]) n <- n - 1 } results$imputed <- cbind(results$imputed, outcome) colnames(results$imputed) <- stringr::str_replace(colnames(results$imputed), "outcome", outcome_var) ## Plot traces of the imputation over iteration results$trace <- ggmice::plot_trace(results$mid) ## Plot correlation between variables results$corr <- ggmice::plot_corr(df, label = TRUE, square = TRUE, rotate = TRUE, diagonal = FALSE) ## Plot histograms of continuous variables results$histogram <- list() for (var in continuous) { results$histogram[[var]] <- ggmice::ggmice(results$mids, ggplot2::aes(x = .data[[var]], group = .imp)) + ggplot2::geom_density() } ## Scatterplots and bar charts for categorical variables results$scatter <- list() results$bar_chart <- list() for (var in categorical) { results$scatter[[var]] <- ggmice::ggmice(results$mids, ggplot2::aes(x = .imp, y = .data[[var]])) + ggplot2::geom_jitter() results$bar_chart[[var]] <- ggmice::ggmice(results$mids, ggplot2::aes(x = .data[[var]], fill = .imp)) + ggplot2::geom_bar(position = "dodge") } results }
## Impute using three different methods using the above impute_data() wrapper imputations = 5 iterations = 5 mice_pmm <- impute_data(method = "pmm", imputations = imputations, iterations = iterations, seed = 684613)

